import mongoose from "mongoose";
const adminSchema = new mongoose.Schema({
    username: {
        type: String,
        required: true,
        unique: true,
    },
    name: {
        type: String,
        required: true,
    },
    mb_no: {
        type: String,
        required: true,
        unique: true,
    },
    email: {
        type: String,
        required: true,
        unique: true,
    },
    password: {
        type: String,
        required: true,
    },
    society_code: {
        type: String,
    },
}, { timestamps: true });
const Admin = mongoose.model('Admin', adminSchema);
export default Admin;
